import{_ as e,c as r}from"./app.81823d03.js";const a={},t=r('<h1 id="ambrosia-kitchen" tabindex="-1"><a class="header-anchor" href="#ambrosia-kitchen" aria-hidden="true">#</a> Ambrosia.Kitchen</h1><h2 id="\u8FD9\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#\u8FD9\u662F\u4EC0\u4E48" aria-hidden="true">#</a> \u8FD9\u662F\u4EC0\u4E48\uFF1F</h2><p>\u672C\u9879\u76EE\u53D7\u5230\u4E86<a href="https://github.com/Anduin2017/HowToCook" target="_blank" rel="noopener noreferrer">\u7A0B\u5E8F\u5458\u505A\u996D\u6307\u5357</a>\u7684\u542F\u53D1, \u81F4\u529B\u4E8E\u6784\u5EFA\u4E00\u4E2A\u4EE5\u7A0B\u5E8F\u5458\u89D2\u5EA6\u6765\u770B\u5F85\u83DC\u8C31\u7684\u4ED3\u5E93.</p><p>\u672C\u9879\u76EE\u4E2D\u7684\u83DC\u8C31\u5747\u4F7F\u7528 <a href="https://cooklang.org" target="_blank" rel="noopener noreferrer">Cooklang</a> \u7F16\u5199, \u5728\u5176\u57FA\u7840\u4E0A\u6709\u5C11\u91CF\u7684\u4F18\u5316\u4EE5\u63D0\u9AD8\u53EF\u8BFB\u6027.</p><h2 id="\u4E3A\u4EC0\u4E48\u53C8\u505A\u4E00\u4E2A\u83DC\u8C31\u4ED3\u5E93" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u53C8\u505A\u4E00\u4E2A\u83DC\u8C31\u4ED3\u5E93" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48\u53C8\u505A\u4E00\u4E2A\u83DC\u8C31\u4ED3\u5E93</h2><p>Github \u4E0A\u4E5F\u6709\u4E00\u4E9B\u5176\u4ED6\u7684\u98DF\u8C31\u7C7B\u4ED3\u5E93, \u4E5F\u90FD\u8BF4\u81EA\u5DF1\u5F00\u6E90. \u4F46\u5728\u6211\u770B\u6765, \u98DF\u8C31\u672C\u8EAB\u5E76\u4E0D\u662F\u5F00\u6E90\u7684, \u5B83\u4EEC\u53EA\u662F\u5F88\u591A\u64CD\u4F5C\u6B65\u9AA4\u7684\u4E00\u4E2A\u96C6\u5408, \u662F\u4E00\u4E9B\u8BF4\u660E, \u800C\u4E0D\u662F\u50CF\u5F00\u6E90\u9879\u76EE\u4E00\u6837\u662F\u4E00\u4E2A\u53EF\u88AB\u4F9D\u8D56\u548C\u4F7F\u7528\u7684\u4E1C\u897F.</p><p>\u672C\u9879\u76EE\u4E2D, \u5C06\u7740\u91CD\u4E8E\u90A3\u4E9B<strong>\u53EF\u590D\u7528\u7684\u7EC4\u4EF6</strong>, \u800C\u4E0D\u662F\u9700\u8981\u9075\u7167\u7684\u6307\u5F15. \u6211\u4EEC\u5C06\u5BF9\u83DC\u8C31\u8FDB\u884C\u62C6\u5206\u91CD\u7EC4, \u62BD\u8C61\u5206\u79BB\u51FA\u53EF\u91CD\u7528\u7684\u7EC4\u4EF6, \u5E76\u63D0\u4F9B\u4E00\u4E9B\u5229\u7528\u8FD9\u4E9B\u7EC4\u4EF6\u7EC4\u88C5\u51FA\u6765\u7684\u6599\u7406\u4F5C\u4E3A\u793A\u4F8B. \u5728\u65E5\u5E38\u751F\u6D3B\u4E2D, \u6211\u4EEC\u53EF\u4EE5\u53BB\u51C6\u5907\u4E00\u4E9B\u8FD9\u6837\u7684\u7EC4\u4EF6, \u8FD9\u6837\u5728\u5173\u952E\u65F6\u5019, \u53EF\u4EE5\u5F88\u5FEB\u5F88\u8F7B\u677E\u7684\u6784\u5EFA\u51FA\u4E00\u684C\u4E30\u76DB\u800C\u53C8\u7F8E\u5473\u7684\u4F73\u80B4.</p><p>\u540C\u65F6, \u5728\u9879\u76EE\u5F00\u53D1\u8FC7\u7A0B\u4E2D, \u6211\u4EEC\u5C06\u4F1A\u6574\u7406\u4E00\u6279\u53C2\u6570\u98DF\u8C31, \u8FD9\u4E9B\u98DF\u8C31\u5C31\u662F\u4E00\u4E2A\u53EF\u88AB\u590D\u7528\u7684\u51FD\u6570, \u7528\u6237\u53EF\u4EE5\u5C06\u5176\u4ED6\u98DF\u6750\u66FF\u4EE3\u8FDB\u53BB, \u521B\u9020\u5C5E\u4E8E\u81EA\u5DF1\u7684\u83DC\u80B4.</p>',8);function n(o,h){return t}var c=e(a,[["render",n]]);export{c as default};
