import{_ as a,c as e}from"./app.372aa7dd.js";const r={},h=e('<h1 id="\u6838\u5FC3\u98DF\u6750" tabindex="-1"><a class="header-anchor" href="#\u6838\u5FC3\u98DF\u6750" aria-hidden="true">#</a> \u6838\u5FC3\u98DF\u6750</h1><h2 id="\u8089\u7C7B" tabindex="-1"><a class="header-anchor" href="#\u8089\u7C7B" aria-hidden="true">#</a> \u8089\u7C7B</h2><p>\u4F9D\u7167\u4F20\u7EDF\u505A\u6CD5\uFF0C\u8981\u628A\u725B\u6392\u505A\u597D\u9700\u8981\u76F8\u5F53\u7684\u7ECF\u9A8C\u3002\u53A8\u5E08\u9700\u8981\u953B\u70BC\u81EA\u5DF1\u5BF9\u706B\u5019\u548C\u719F\u5EA6\u7684\u76F4\u89C9\u3002\u5373\u4F7F\u662F\u725B\u6392\u70E7\u70E4\u4E13\u5BB6\uFF0C\u5076\u5C14\u4E5F\u4F1A\u5931\u624B\u3002\u5373\u4F7F\u6CA1\u5931\u624B\uFF0C\u725B\u6392\u4E2D\u5FC3\u90E8\u4F4D\u8FBE\u5230\u6700\u4F73\u6E29\u5EA6\u65F6\uFF0C\u725B\u6392\u8868\u9762\u4E00\u5B9A\u662F\u8FC7\u719F\u7684\u3002\u8FD9\u662F\u9AD8\u6E29\u70F9\u8C03\u7684\u5FC5\u7136\u73B0\u8C61\u3002</p><p>\u501F\u7531\u771F\u7A7A\u4F4E\u6E29\u8FD9\u7C7B\u8BB2\u7A76\u7CBE\u786E\u4F4E\u6E29\u7684\u65B0\u9896\u65B9\u6CD5\uFF0C\u505A\u51FA\u8D28\u4F73\u5473\u7F8E\u7684\u725B\u6392\u5DF2\u4E0D\u518D\u662F\u96BE\u4E8B\u3002\u5F53\u7136\uFF0C\u7531\u4E8E\u808B\u773C\u3001\u91CC\u810A\u7B49\u90E8\u4F4D\u7684\u5DEE\u5F02\u548C\u6BCF\u5757\u725B\u8089\u6CB9\u8102\u4E0E\u808C\u8089\u7EA4\u7EF4\u6BD4\u4F8B\u7684\u5DEE\u522B\uFF0C\u7EC6\u5FAE\u7684\u5DEE\u5F02\u4ECD\u7136\u5728\u6240\u96BE\u514D\u3002\u4E0D\u8FC7\uFF0C\u4EE5\u771F\u7A7A\u4F4E\u6E29\u70F9\u8C03\u65F6\uFF0C\u725B\u8089\u56E0\u84B8\u53D1\u800C\u6D41\u5931\u7684\u6C34\u5206\u4EC5 3% - 5%\uFF0C\u7EA6\u4F20\u7EDF\u505A\u6CD5\u6D41\u5931\u91CF\u7684\u5341\u5206\u4E4B\u4E00\u3002\u56E0\u800C\u4F7F\u7528\u771F\u7A7A\u4F4E\u6E29\u70F9\u8C03\u867D\u7136\u8017\u65F6\uFF0C\u4F46\u80FD\u5927\u5E45\u63D0\u9AD8\u591A\u6C41\u7A0B\u5EA6\u548C\u54C1\u8D28\u7684\u7A33\u5B9A\u6027\u3002</p><h3 id="\u719F\u6210\u725B\u8089" tabindex="-1"><a class="header-anchor" href="#\u719F\u6210\u725B\u8089" aria-hidden="true">#</a> \u719F\u6210\u725B\u8089</h3><p>\u5E72\u5F0F\u719F\u6210\u725B\u8089\u6781\u53D7\u597D\u8BC4\u3002\u8FD9\u79CD\u725B\u8089\u5177\u6709\u5B9C\u4EBA\u7684\u575A\u679C\u9999\u6C14\u548C\u6E29\u548C\u7684\u4E73\u916A\u9999\u8C03\uFF0C\u4E3B\u8981\u6765\u81EA\u8102\u80AA\u6C27\u5316\u4EE5\u53CA\u7CD6\u548C\u86CB\u767D\u8D28\u7684\u6D53\u7F29\u6548\u5E94\u3002\u9175\u7D20\u628A\u808C\u8089\u7EA4\u7EF4\u5206\u89E3\u6210\u7247\u6BB5\u7684\u86CB\u767D\u8D28\uFF0C\u70F9\u8C03\u65F6\u53EF\u4FDD\u7559\u66F4\u591A\u7684\u81EA\u7136\u539F\u6C41\uFF0C\u8089\u7C7B\u4E5F\u56E0\u6B64\u66F4\u8F6F\u5AE9\u3002</p><p>\u4E0D\u8FC7\u5E72\u5F0F\u719F\u6210\u7684\u6210\u672C\u76F8\u5F53\u9AD8\uFF0C\u4E0D\u4EC5\u8D39\u65F6\uFF0C\u8FD8\u9700\u8981\u63A7\u5236\u6E29\u5EA6\u548C\u6E7F\u5EA6\u7684\u50A8\u5B58\u8BBE\u5907\u3002\u5E72\u5F0F\u719F\u6210\u6CD5\u4F1A\u79FB\u9664\u8089\u54C1\u4E2D\u7684\u6C34\u5206\uFF0C\u56E0\u6B64\u8089\u7C7B\u4F53\u79EF\u4F1A\u660E\u663E\u7F29\u5C0F\u3002\u6B64\u5916\uFF0C\u8089\u54C1\u7684\u8868\u5C42\u4E5F\u5FC5\u987B\u4E22\u5F03\uFF0C\u56E0\u4E3A\u8FD9\u90E8\u5206\u5B9E\u9645\u4E0A\u5DF2\u7ECF\u8150\u574F\u3002\u6240\u4EE5\u8FD9\u5757\u725B\u6392\u5C3D\u7BA1\u7F8E\u5473\uFF0C\u4EE3\u4EF7\u5374\u5341\u5206\u9AD8\u6602\u3002</p><h2 id="\u9C7C\u7C7B" tabindex="-1"><a class="header-anchor" href="#\u9C7C\u7C7B" aria-hidden="true">#</a> \u9C7C\u7C7B</h2><p>\u8089\u7C7B\u548C\u6D77\u9C9C\u7684\u7406\u60F3\u70F9\u8C03\u6E29\u5EA6, \u4E00\u5B9A\u7A0B\u5EA6\u4E0A\u53D6\u51B3\u4E8E\u52A8\u7269\u7684\u4F53\u6E29. \u54FA\u4E73\u7C7B\u5927\u591A\u63A5\u8FD137\u2103. \u800C\u9C7C\u7C7B\u5C45\u4F4F\u5728\u6BD4\u8F83\u51C9\u5FEB\u7684\u6C34\u4E2D, \u4F53\u6E29\u4E5F\u4F4E\u5F97\u591A, \u4ECE\u4E24\u6781\u5730\u533A\u76845\u2103\u5230\u8D64\u9053\u6C34\u57DF\u768430\u2103. \u52A0\u70ED\u5230\u67D0\u4E2A\u6E29\u5EA6, \u53EF\u80FD\u5927\u591A\u6570\u8089\u7C7B\u8FD8\u662F\u751F\u7684, \u4F46\u9C7C\u7C7B\u7684\u86CB\u767D\u8D28\u5C31\u5DF2\u7ECF\u5B8C\u5168\u51DD\u7ED3, \u8089\u8D28\u4E5F\u53D8\u5F97\u53C8\u5E72\u53C8\u7C89.</p><p>\u4E0D\u8FC7, \u7528\u7CBE\u786E\u7684\u4F4E\u6E29\u70F9\u8C03\u6280\u672F, \u53EF\u4EE5\u628A\u9C7C\u716E\u7684\u975E\u5E38\u67D4\u6ED1, \u540C\u65F6\u4FDD\u7559\u751C\u7F8E\u7684\u6D77\u5473. \u5982\u679C\u4F60\u89C9\u5F97\u4E0D\u559C\u6B22\u5403\u9C7C, \u90A3\u4E0D\u59A8\u7528\u8FD9\u91CC\u7684\u65B9\u6CD5\u518D\u8BD5\u4E00\u6B21.</p><p>\u5927\u591A\u6570\u9C7C\u7C7B\u7684\u6700\u4F73\u70F9\u8C03\u6E29\u5EA6\u4E3A 42-50 \u2103. \u9700\u8981\u6CE8\u610F\u7684\u662F, \u8FD9\u4E2A\u6E29\u5EA6\u4E0D\u8DB3\u4EE5\u6740\u83CC. \u4F46\u5373\u4F7F\u4EE5\u9AD8\u6E29\u70F9\u716E, \u4E00\u5B9A\u4F1A\u628A\u9C7C\u716E\u8FC7\u5934, \u5374\u4E0D\u4E00\u5B9A\u66F4\u5B89\u5168. \u98DF\u7528\u719F\u7684\u6070\u5230\u597D\u5904\u5374\u672A\u5B8C\u5168\u6740\u83CC\u7684\u9C7C\u8089, \u7684\u786E\u6709\u5C11\u8BB8\u98CE\u9669, \u4F46\u5927\u591A\u6570\u4EBA\u7684\u514D\u75AB\u80FD\u529B\u662F\u5B8C\u5168\u53EF\u4EE5\u627F\u53D7\u7684.</p><h2 id="\u86CB\u7C7B\u6599\u7406" tabindex="-1"><a class="header-anchor" href="#\u86CB\u7C7B\u6599\u7406" aria-hidden="true">#</a> \u86CB\u7C7B\u6599\u7406</h2><p>\u86CB\u53EF\u4EE5\u505A\u6210\u6C34\u6CE2\u86CB\uFF0C\u7092\u86CB\uFF0C\u592A\u9633\u86CB\uFF0C\u8377\u5305\u86CB\uFF0C\u70B8\u86CB\uFF0C\u6C34\u716E\u86CB\uFF0C\u86CB\u5377\uFF0C\u6CD5\u5F0F\u7126\u7CD6\u5E03\u4E01\uFF0C\u610F\u5F0F\u70D8\u86CB\u7B49\u6599\u7406\u3002\u7F8E\u5473\u800C\u53D8\u5316\u65E0\u7A77\u3002</p><p>\u86CB\u662F\u6700\u80FD\u4F53\u73B0\u7CBE\u786E\u6E29\u5EA6\u91CD\u8981\u6027\u7684\u98DF\u6750\u3002\u86CB\u9EC4\u7684\u53E3\u5473\u548C\u8D28\u5730\u662F\u8F6F\u6ED1\u6D41\u52A8\u8FD8\u662F\u786C\u5982\u6A61\u76AE\uFF0C\u5173\u952E\u5F80\u5F80\u53EA\u5728\u4E8E\u5DEE\u5F02\u6781\u4E3A\u7EC6\u5C0F\u7684\u70F9\u716E\u6E29\u5EA6\u3002</p><p>\u5728\u8FD9\u91CC\u6211\u4EEC\u4F1A\u7528\u5230\u6052\u6E29\u6C34\u69FD\u3002\u53EA\u8981\u719F\u6089\u5F62\u6210\u6BCF\u79CD\u72B6\u6001\u7684\u6E29\u5EA6\uFF0C\u5C31\u80FD\u968F\u5FC3\u6240\u6B32\u4E14\u6070\u5230\u597D\u5904\u7684\u505A\u51FA\u5404\u79CD\u8D28\u5730\u7684\u86CB\uFF0C\u4E0D\u8BBA\u662F\u4E73\u8102\u72B6\uFF0C\u5361\u4ED5\u8FBE\u6216\u662F\u8F6F\u7CD6\u72B6\u90FD\u884C\u3002</p>',15);function d(i,n){return h}var p=a(r,[["render",d],["__file","index.html.vue"]]);export{p as default};
