import{_ as e,c as a}from"./app.372aa7dd.js";const r={},d=a('<h1 id="\u6210\u54C1\u83DC" tabindex="-1"><a class="header-anchor" href="#\u6210\u54C1\u83DC" aria-hidden="true">#</a> \u6210\u54C1\u83DC</h1><p>\u4F20\u7EDF\u70F9\u8C03\u6CD5\u5219\u4E2D, \u98DF\u7269\u5F80\u5F80\u662F\u653E\u5728\u4E00\u4E2A\u5927\u6D45\u76D8\u91CC\u7AEF\u4E0A\u9910\u684C, \u7136\u540E\u7531\u7528\u9910\u8005\u6216\u670D\u52A1\u751F\u628A\u98DF\u7269\u5206\u5230\u6BCF\u4E2A\u4EBA\u7684\u9910\u789F\u91CC\u7684. \u8FD9\u79CD\u65B9\u5F0F\u5728\u4E16\u754C\u4E0A\u8BB8\u591A\u70F9\u8C03\u6587\u5316\u4E2D\u90FD\u662F\u76F8\u540C\u7684. \u6BD4\u5982, \u4F20\u7EDF\u7684\u4E2D\u9910\u5C31\u91C7\u7528\u8FD9\u6837\u7684\u65B9\u5F0F, \u4EBA\u4EEC\u628A\u5927\u76D8\u98DF\u7269\u653E\u5728\u684C\u4E0A, \u6BCF\u4E2A\u4EBA\u90FD\u53EF\u4EE5\u968F\u81EA\u5DF1\u7684\u610F\u613F\u53BB\u9009\u62E9\u83DC\u80B4. \u8FD9\u79CD\u5BB6\u5EAD\u5F0F\u7684\u8FDB\u98DF\u65B9\u5F0F\u5728\u610F\u5927\u5229\u3001\u5FB7\u56FD\u548C\u7F8E\u56FD\u4E5F\u5F88\u4E3A\u5E38\u89C1.</p><p>\u7136\u800C\u5230\u4E16\u754C\u4E0A\u4EFB\u4F55\u4E00\u4E2A\u5730\u65B9\u7684\u4F18\u79C0\u9910\u9986, \u4F60\u90FD\u4F1A\u53D1\u73B0\u6709\u4E9B\u98DF\u7269\u662F\u51E0\u79CD\u4E00\u8D77\u6446\u653E\u5728\u4E00\u4E2A\u9910\u789F\u91CC, \u7136\u540E\u7AEF\u4E0A\u9910\u684C\u7684, \u6709\u4E9B\u53A8\u5E08\u79F0\u4E4B\u4E3A\u201C\u76D8\u9910\u201D(plated dish). \u8FD9\u79CD\u65B9\u5F0F\u5728\u5982\u4ECA\u5341\u5206\u5E38\u89C1, \u800C\u4E14\u98DF\u7269\u642D\u914D\u662F\u73B0\u5728\u8BB8\u591A\u9AD8\u7EA7\u70F9\u8C03\u7684\u4E3B\u9898. \u751A\u81F3\u6709\u4EBA\u89C9\u5F97\u897F\u9910\u53A8\u5E08\u7684\u65E5\u5E38\u5DE5\u4F5C\u5C31\u662F\u6446\u76D8. \u8FD9\u5176\u5B9E\u662F 20 \u4E16\u7EAA 60 \u5E74\u4EE3\u624D\u5F00\u59CB\u6709\u7684\u521B\u65B0.</p><p>\u5728\u7C7B\u4F3C\u989C\u6599\u677F\u7684\u5927\u74F7\u789F\u4E2D\u8FDB\u884C\u6446\u76D8, \u7ED9\u4E86\u53A8\u5E08\u4EEC\u66F4\u591A\u7684\u7A7A\u95F4, \u5E76\u5141\u8BB8\u4ED6\u4EEC\u521B\u9020\u66F4\u4E3A\u590D\u6742\u7684\u7F8E\u98DF. \u540C\u65F6, \u7531\u4E8E\u98DF\u7269\u88AB\u6446\u653E\u5728\u4E00\u4E2A\u76D8\u5B50\u91CC, \u4EBA\u4EEC\u53EF\u4EE5\u66F4\u8F7B\u677E\u5730\u54C1\u5C1D\u5230\u5404\u79CD\u4E0D\u540C\u7684\u5473\u9053\u548C\u8D28\u5730, \u4ECE\u800C\u521B\u9020\u51FA\u4E00\u79CD\u5168\u65B0\u7684\u5473\u89C9\u4F53\u9A8C(\u8FD9\u6837\u505A\u4E5F\u80FD\u964D\u4F4E\u98DF\u7269\u7684\u6574\u4F53 GI \u503C).</p><p>\u6211\u4EEC\u9009\u62E9\u8FD9\u6837\u7684\u65B9\u5F0F\u6765\u5C55\u73B0\u6210\u54C1\u83DC\u8C31, \u4E3B\u8981\u662F\u4E3A\u4E86\u63D0\u9AD8\u83DC\u8C31\u7684\u517C\u5BB9\u6027, \u6211\u4EEC\u5F3A\u8C03\u6210\u54C1\u83DC\u662F\u7531\u591A\u79CD\u57FA\u7840\u98DF\u6750\u7EC4\u5408\u800C\u6210, \u800C\u4E0D\u662F\u4E00\u6B21\u6027\u505A\u51FA\u6765\u7684. \u8FD9\u6837\u7684\u8BDD, \u4F60\u4E5F\u80FD\u66FF\u6362\u5176\u4E2D\u4E0D\u540C\u7684\u7EC4\u4EF6, \u6765\u642D\u914D\u65B0\u7684\u83DC\u8C31.</p><h2 id="\u6C99\u62C9" tabindex="-1"><a class="header-anchor" href="#\u6C99\u62C9" aria-hidden="true">#</a> \u6C99\u62C9</h2><p>\u5728\u5927\u4F17\u7684\u523B\u677F\u5370\u8C61\u4E2D\uFF0C\u6C99\u62C9\u5C31\u662F\u4E00\u5806\u7EFF\u8272\u83DC\u53F6\uFF0C\u6709\u4E9B\u4EBA\u5632\u7B11\u8FD9\u662F\u201C\u5154\u5B50\u5403\u7684\u201D\u4E1C\u897F\u3002\u4F46\u6C99\u62C9\u8FDC\u4E0D\u53EA\u662F\u7EFF\u8272\u83DC\u53F6\u3002\u6211\u4EEC\u53EF\u4EE5\u7528\u6C99\u62C9\u5728\u4E00\u987F\u996D\u4E2D\u521B\u9020\u5438\u5F15\u4EBA\u7684\u8D28\u611F\u5BF9\u6BD4\u548C\u98CE\u5473\u5BF9\u6BD4\u3002</p><h2 id="\u51B7\u6C64" tabindex="-1"><a class="header-anchor" href="#\u51B7\u6C64" aria-hidden="true">#</a> \u51B7\u6C64</h2><p>\u5F88\u591A\u4EBA\u8BA4\u4E3A\u4E30\u76DB\u3001\u6E29\u6696\u4EBA\u5FC3\u53C8\u6EDA\u70EB\u7684\u624D\u662F\u7406\u60F3\u7684\u6C64\u3002\u4F46\u6C64\u5373\u4F7F\u4E0D\u70ED\u4E5F\u540C\u6837\u80FD\u8BA9\u4EBA\u611F\u5230\u6EE1\u8DB3\u3002\u6D53\u7A20\u3001\u63D0\u795E\u53C8\u7E41\u590D\u7684\u51B7\u70EB\uFF0C\u5728\u610F\u5927\u5229\u3001\u897F\u73ED\u7259\u3001\u6CD5\u56FD\u3001\u5317\u6B27\u548C\u4FC4\u7F57\u65AF\u90FD\u5F88\u53D7\u6B22\u8FCE\u3002</p>',9);function h(t,i){return d}var c=e(r,[["render",h],["__file","index.html.vue"]]);export{c as default};
